  1â†’use std::sync::Arc;
  2â†’
  3â†’use tokio::sync::mpsc;
  4â†’
  5â†’use leeson::LeesonError;
  6â†’use leeson::auth::validate_credentials;
  7â†’use leeson::config::fetch_config;
  8â†’use leeson::models::Channel;
  9â†’use leeson::models::book::BookDepth;
 10â†’use leeson::tls::build_tls_config;
 11â†’use leeson::tui::{self, App, Message};
 12â†’use leeson::websocket::{ConnectionCommand, ConnectionManager, subscribe, subscribe_book};
 13â†’
 14â†’#[tokio::main]
 15â†’async fn main() -> Result<(), LeesonError> {
 16â†’    // Set up file logging (logs to leeson.log)
 17â†’    let file = std::fs::File::create("leeson.log")
 18â†’        .map_err(|e| LeesonError::Io(format!("failed to create log file: {e}")))?;
 19â†’    tracing_subscriber::fmt()
 20â†’        .with_max_level(tracing::Level::DEBUG)
 21â†’        .with_writer(file)
 22â†’        .with_ansi(false)
 23â†’        .init();
 24â†’
 25â†’    let app_config = fetch_config()?;
 26â†’    let tls_config = Arc::new(build_tls_config()?);
 27â†’
 28â†’    let has_credentials = matches!(
 29â†’        (&app_config.kraken.api_key, &app_config.kraken.api_secret),
 30â†’        (Some(k), Some(s)) if !k.is_empty() && !s.is_empty()
 31â†’    );
 32â†’
 33â†’    // Validate API credentials if provided
 34â†’    let (credentials_valid, auth_error) = if has_credentials {
 35â†’        let key = app_config.kraken.api_key.as_ref().unwrap();
 36â†’        let secret = app_config.kraken.api_secret.as_ref().unwrap();
 37â†’        match validate_credentials(key, secret, (*tls_config).clone()).await {
 38â†’            Ok(_) => (true, None),
 39â†’            Err(e) => (false, Some(e.to_string())),
 40â†’        }
 41â†’    } else {
 42â†’        (false, None)
 43â†’    };
 44â†’
 45â†’    // Use authenticated endpoint if credentials are valid, otherwise use configured URL
 46â†’    // The balances channel requires the authenticated endpoint
 47â†’    let url = if credentials_valid {
 48â†’        "wss://ws-auth.kraken.com/v2".to_string()
 49â†’    } else {
 50â†’        app_config.kraken.websocket_url.clone()
 51â†’    };
 52â†’
 53â†’    // Setup terminal
 54â†’    let mut terminal = tui::setup_terminal()?;
 55â†’
 56â†’    // Create application state
 57â†’    let mut app = App::new();
 58â†’    app.authenticated = credentials_valid;
 59â†’
 60â†’    // Show auth error if credentials were provided but invalid
 61â†’    if let Some(error) = auth_error {
 62â†’        app.show_error(format!("Auth failed: {}", error));
 63â†’    }
 64â†’
 65â†’    // Create message channel (shared with connection manager)
 66â†’    let (tx, mut rx) = mpsc::unbounded_channel::<Message>();
 67â†’
 68â†’    // Create command channel for subscription tracking
 69â†’    let (cmd_tx, cmd_rx) = mpsc::unbounded_channel::<ConnectionCommand>();
 70â†’
 71â†’    // Shared writer: connection manager writes, main loop reads
 72â†’    let writer: Arc<tokio::sync::Mutex<Option<leeson::websocket::WsWriter>>> =
 73â†’        Arc::new(tokio::sync::Mutex::new(None));
 74â†’
 75â†’    // Spawn the connection manager
 76â†’    let manager = ConnectionManager::new(
 77â†’        url,
 78â†’        tls_config,
 79â†’        app_config.kraken.api_key.clone(),
 80â†’        app_config.kraken.api_secret.clone(),
 81â†’        tx.clone(),
 82â†’        writer.clone(),
 83â†’        cmd_rx,
 84â†’    );
 85â†’    tokio::spawn(async move { manager.run().await });
 86â†’
 87â†’    // Spawn event reader for keyboard input
 88â†’    tui::event::spawn_event_reader(tx.clone());
 89â†’
 90â†’    // Spawn tick timer for periodic updates
 91â†’    tui::event::spawn_tick_timer(tx.clone(), 250);
 92â†’
 93â†’    // Main event loop
 94â†’    loop {
 95â†’        // Render UI
 96â†’        terminal
 97â†’            .draw(|frame| tui::render(frame, &app))
 98â†’            .map_err(|e| LeesonError::Io(e.to_string()))?;
 99â†’
100â†’        // Check if we should quit
101â†’        if app.should_quit {
102â†’            break;
103â†’        }
104â†’
105â†’        // Wait for next message
106â†’        if let Some(message) = rx.recv().await {
107â†’            // Handle actions that require WebSocket writes
108â†’            if let Some(action) = tui::event::update(&mut app, message) {
109â†’                match action {
110â†’                    tui::event::Action::SubscribePair(symbol) => {
111â†’                        let mut guard = writer.lock().await;
112â†’                        if let Some(ref mut w) = *guard {
113â†’                            let symbols = vec![symbol.clone()];
114â†’                            let _ = subscribe(w, &Channel::Ticker, &symbols, None).await;
115â†’                            let _ = subscribe_book(w, &symbols, BookDepth::D25, None).await;
116â†’                            let _ = subscribe(w, &Channel::Candles, &symbols, None).await;
117â†’                            let _ = subscribe(w, &Channel::Trades, &symbols, None).await;
118â†’                        }
119â†’                        let _ = cmd_tx.send(ConnectionCommand::PairSubscribed(symbol));
120â†’                    }
121â†’                    tui::event::Action::UnsubscribePair(symbol) => {
122â†’                        let _ = cmd_tx.send(ConnectionCommand::PairUnsubscribed(symbol));
123â†’                    }
124â†’                    tui::event::Action::SendToAgent1(message) => {
125â†’                        // Show user's message in Agent 1 output
126â†’                        app.add_agent_output(0, format!("You: {}", message));
127â†’                        // TODO: Send message to Agent 1 process and stream response
128â†’                        app.add_agent_output(
129â†’                            0,
130â†’                            "Agent 1: [Awaiting agent integration]".to_string(),
131â†’                        );
132â†’                    }
133â†’                    tui::event::Action::PlaceOrder => {
134â†’                        // TODO: Implement order placement
135â†’                    }
136â†’                    tui::event::Action::CancelOrder(_order_id) => {
137â†’                        // TODO: Implement order cancellation
138â†’                    }
139â†’                }
140â†’            }
141â†’        }
142â†’    }
143â†’
144â†’    // Restore terminal
145â†’    tui::restore_terminal(&mut terminal)?;
146â†’
147â†’    Ok(())
148â†’}
