  1→//! Event handling for the TUI.
  2→
  3→use std::time::Duration;
  4→
  5→use crossterm::event::{self, Event as CrosstermEvent, KeyCode, KeyEvent, KeyModifiers};
  6→use tokio::sync::mpsc;
  7→
  8→use crate::models::balance::BalanceResponse;
  9→use crate::models::book::BookUpdateResponse;
 10→use crate::models::candle::CandleUpdateResponse;
 11→use crate::models::execution::ExecutionUpdateResponse;
 12→use crate::models::ticker::TickerUpdateResponse;
 13→use crate::models::trade::TradeUpdateResponse;
 14→use crate::models::{
 15→    AddOrderResponse, AmendOrderResponse, CancelAllResponse, CancelOrderResponse,
 16→    StatusUpdateResponse,
 17→};
 18→
 19→use super::app::{App, AssetBalance, Focus, MAX_ORDERBOOK_HISTORY, Mode, OrderBookSnapshot, Tab};
 20→
 21→/// Events that can occur in the application.
 22→#[derive(Debug)]
 23→pub enum Event {
 24→    /// A key was pressed.
 25→    Key(KeyEvent),
 26→    /// Terminal was resized.
 27→    Resize(u16, u16),
 28→    /// Periodic tick for UI updates.
 29→    Tick,
 30→}
 31→
 32→/// Messages that update application state.
 33→#[derive(Debug)]
 34→pub enum Message {
 35→    /// Input event from terminal.
 36→    Input(Event),
 37→
 38→    /// Ticker update from WebSocket.
 39→    Ticker(TickerUpdateResponse),
 40→    /// Order book update from WebSocket.
 41→    Book(BookUpdateResponse),
 42→    /// Trade update from WebSocket.
 43→    Trade(TradeUpdateResponse),
 44→    /// Candle update from WebSocket.
 45→    Candle(CandleUpdateResponse),
 46→    /// Execution update from WebSocket.
 47→    Execution(ExecutionUpdateResponse),
 48→    /// Balance update from WebSocket.
 49→    Balance(BalanceResponse),
 50→    /// Status update from WebSocket.
 51→    Status(StatusUpdateResponse),
 52→    /// Heartbeat received.
 53→    Heartbeat,
 54→
 55→    /// Order placement response.
 56→    OrderPlaced(AddOrderResponse),
 57→    /// Order cancellation response.
 58→    OrderCancelled(CancelOrderResponse),
 59→    /// Order amendment response.
 60→    OrderAmended(AmendOrderResponse),
 61→    /// Cancel all response.
 62→    AllOrdersCancelled(CancelAllResponse),
 63→
 64→    /// WebSocket connected.
 65→    Connected,
 66→    /// WebSocket disconnected.
 67→    Disconnected,
 68→    /// WebSocket reconnecting.
 69→    Reconnecting,
 70→
 71→    /// Request to quit the application.
 72→    Quit,
 73→}
 74→
 75→/// Spawns a task that polls for terminal events and sends them to a channel.
 76→pub fn spawn_event_reader(tx: mpsc::UnboundedSender<Message>) {
 77→    tokio::spawn(async move {
 78→        loop {
 79→            // Poll for events with a 50ms timeout
 80→            match tokio::task::spawn_blocking(|| {
 81→                if event::poll(Duration::from_millis(50)).unwrap_or(false) {
 82→                    event::read().ok()
 83→                } else {
 84→                    None
 85→                }
 86→            })
 87→            .await
 88→            {
 89→                Ok(Some(CrosstermEvent::Key(key))) => {
 90→                    if tx.send(Message::Input(Event::Key(key))).is_err() {
 91→                        break;
 92→                    }
 93→                }
 94→                Ok(Some(CrosstermEvent::Resize(w, h))) => {
 95→                    if tx.send(Message::Input(Event::Resize(w, h))).is_err() {
 96→                        break;
 97→                    }
 98→                }
 99→                Ok(_) => {}
100→                Err(_) => break,
101→            }
102→        }
103→    });
104→}
105→
106→/// Spawns a task that sends periodic tick events.
107→pub fn spawn_tick_timer(tx: mpsc::UnboundedSender<Message>, interval_ms: u64) {
108→    tokio::spawn(async move {
109→        let mut interval = tokio::time::interval(Duration::from_millis(interval_ms));
110→        loop {
111→            interval.tick().await;
112→            if tx.send(Message::Input(Event::Tick)).is_err() {
113→                break;
114→            }
115→        }
116→    });
117→}
118→
119→/// Updates application state based on a message.
120→pub fn update(app: &mut App, message: Message) -> Option<Action> {
121→    match message {
122→        Message::Input(event) => handle_input(app, event),
123→        Message::Ticker(response) => {
124→            for data in response.data {
125→                app.update_ticker(data.symbol.clone(), data);
126→            }
127→            None
128→        }
129→        Message::Book(response) => {
130→            let is_snapshot = response.tpe == "snapshot";
131→
132→            for data in response.data {
133→                let state = app.orderbooks.entry(data.symbol.clone()).or_default();
134→
135→                if is_snapshot {
136→                    // Snapshot: replace entire book
137→                    state.bids = data.bids;
138→                    state.asks = data.asks;
139→                } else {
140→                    // Update: apply incremental changes
141→                    // A level with qty=0 means remove that price level
142→                    for level in data.bids {
143→                        if level.qty == rust_decimal::Decimal::ZERO {
144→                            state.bids.retain(|b| b.price != level.price);
145→                        } else if let Some(existing) =
146→                            state.bids.iter_mut().find(|b| b.price == level.price)
147→                        {
148→                            existing.qty = level.qty;
149→                        } else {
150→                            state.bids.push(level);
151→                            // Keep bids sorted highest to lowest
152→                            state.bids.sort_by(|a, b| b.price.cmp(&a.price));
153→                        }
154→                    }
155→                    for level in data.asks {
156→                        if level.qty == rust_decimal::Decimal::ZERO {
157→                            state.asks.retain(|a| a.price != level.price);
158→                        } else if let Some(existing) =
159→                            state.asks.iter_mut().find(|a| a.price == level.price)
160→                        {
161→                            existing.qty = level.qty;
162→                        } else {
163→                            state.asks.push(level);
164→                            // Keep asks sorted lowest to highest
165→                            state.asks.sort_by(|a, b| a.price.cmp(&b.price));
166→                        }
167→                    }
168→                }
169→
170→                state.checksum = data.checksum;
171→                state.last_update = Some(std::time::Instant::now());
172→
173→                // Capture snapshot for history after updating state
174→                if let (Some(best_bid), Some(best_ask)) = (state.bids.first(), state.asks.first()) {
175→                    let snapshot = OrderBookSnapshot {
176→                        timestamp: data.timestamp.clone(),
177→                        best_bid: best_bid.price,
178→                        best_ask: best_ask.price,
179→                        spread: best_ask.price - best_bid.price,
180→                    };
181→                    if state.history.len() >= MAX_ORDERBOOK_HISTORY {
182→                        state.history.pop_front();
183→                    }
184→                    state.history.push_back(snapshot);
185→                }
186→            }
187→            None
188→        }
189→        Message::Trade(response) => {
190→            for data in response.data {
191→                let symbol = data.symbol.clone();
192→                app.add_trade(&symbol, data);
193→            }
194→            None
195→        }
196→        Message::Candle(response) => {
197→            for data in response.data {
198→                let candles = app
199→                    .candles
200→                    .entry(data.symbol.clone())
201→                    .or_insert_with(|| std::collections::VecDeque::with_capacity(100));
202→                if candles.len() >= 100 {
203→                    candles.pop_front();
204→                }
205→                candles.push_back(data);
206→            }
207→            None
208→        }
209→        Message::Execution(response) => {
210→            for data in response.data {
211→                // Add to open or executed orders based on status
212→                let symbol = data.symbol.clone();
213→                match data.order_status.as_str() {
214→                    "open" | "pending" | "new" => {
215→                        let orders = app.open_orders.entry(symbol).or_default();
216→                        // Update or add
217→                        if let Some(pos) = orders.iter().position(|o| o.order_id == data.order_id) {
218→                            orders[pos] = data;
219→                        } else {
220→                            orders.push(data);
221→                        }
222→                    }
223→                    "filled" | "canceled" | "expired" => {
224→                        // Remove from open orders
225→                        if let Some(orders) = app.open_orders.get_mut(&symbol) {
226→                            orders.retain(|o| o.order_id != data.order_id);
227→                        }
228→                        // Add to executed orders
229→                        let executed = app
230→                            .executed_orders
231→                            .entry(symbol)
232→                            .or_insert_with(|| std::collections::VecDeque::with_capacity(100));
233→                        if executed.len() >= 100 {
234→                            executed.pop_front();
235→                        }
236→                        executed.push_back(data);
237→                    }
238→                    _ => {}
239→                }
240→            }
241→            None
242→        }
243→        Message::Balance(response) => {
244→            // Process balance snapshot or update
245→            for data in response.data {
246→                let mut spot = rust_decimal::Decimal::ZERO;
247→                let mut earn = rust_decimal::Decimal::ZERO;
248→
249→                for wallet in &data.wallets {
250→                    match wallet.wallet_type.as_str() {
251→                        "spot" => spot = wallet.balance,
252→                        "earn" => earn = wallet.balance,
253→                        _ => {}
254→                    }
255→                }
256→
257→                let balance = AssetBalance {
258→                    asset: data.asset.clone(),
259→                    total: data.balance,
260→                    spot,
261→                    earn,
262→                };
263→                app.asset_balances.insert(data.asset, balance);
264→            }
265→
266→            // Update USD balance field for compatibility
267→            if let Some(usd) = app.asset_balances.get("USD") {
268→                app.balance = usd.total;
269→            }
270→            None
271→        }
272→        Message::Status(_) => {
273→            app.connection_status = super::app::ConnectionStatus::Connected;
274→            None
275→        }
276→        Message::Heartbeat => {
277→            app.last_heartbeat = Some(std::time::Instant::now());
278→            None
279→        }
280→        Message::OrderPlaced(response) => {
281→            if !response.success
282→                && let Some(error) = response.error
283→            {
284→                app.show_error(error);
285→            }
286→            None
287→        }
288→        Message::OrderCancelled(response) => {
289→            if !response.success
290→                && let Some(error) = response.error
291→            {
292→                app.show_error(error);
293→            }
294→            None
295→        }
296→        Message::OrderAmended(response) => {
297→            if !response.success
298→                && let Some(error) = response.error
299→            {
300→                app.show_error(error);
301→            }
302→            None
303→        }
304→        Message::AllOrdersCancelled(_) => None,
305→        Message::Connected => {
306→            app.connection_status = super::app::ConnectionStatus::Connected;
307→            None
308→        }
309→        Message::Disconnected => {
310→            app.connection_status = super::app::ConnectionStatus::Disconnected;
311→            None
312→        }
313→        Message::Reconnecting => {
314→            app.connection_status = super::app::ConnectionStatus::Reconnecting;
315→            None
316→        }
317→        Message::Quit => {
318→            app.should_quit = true;
319→            None
320→        }
321→    }
322→}
323→
324→/// Actions that require external handling (e.g., sending WebSocket messages).
325→#[derive(Debug)]
326→pub enum Action {
327→    /// Send a message to Agent 1.
328→    SendToAgent1(String),
329→    /// Subscribe to a trading pair.
330→    SubscribePair(String),
331→    /// Unsubscribe from a trading pair.
332→    UnsubscribePair(String),
333→    /// Place a new order.
334→    PlaceOrder,
335→    /// Cancel an order.
336→    CancelOrder(String),
337→}
338→
339→/// Handles input events and updates application state.
340→fn handle_input(app: &mut App, event: Event) -> Option<Action> {
341→    match event {
342→        Event::Key(key) => handle_key(app, key),
343→        Event::Resize(_, _) => None,
344→        Event::Tick => {
345→            app.clear_stale_errors();
346→            None
347→        }
348→    }
349→}
350→
351→/// Handles key press events.
352→fn handle_key(app: &mut App, key: KeyEvent) -> Option<Action> {
353→    // Global keys (work in any mode)
354→    match key.code {
